import React, { useEffect, useState } from "react";
import "./Class.css";

function Classes() {
  const [classInfo, setClassInfo] = useState({});

  useEffect(() => {
    const d = {
      className: "ECS150",
      quarter:"WQ2021",
      professor: "Porquet",
      totalStudents: 150,
      sidebar: ["general", "homework-help", "off-topic", "Q&A"]
    };
    setClassInfo(d);
  }, []);

  return (
    <div id="body">
      <div id="sidebar">
        <div className="spacing">
          <div className="classNameSidebar">{classInfo.className}</div>
          <label>{classInfo.quarter}</label>
          <ul>

            {classInfo.sidebar && classInfo.sidebar.map((sidebar) => (
              <li key="sidebar"><button className="">{sidebar}</button></li>
            ))}
          </ul>
        </div>
      </div>
      <div id="main"> MAIN CONTENT </div>
    </div>
  );
}
export default Classes;
