import React, { useEffect, useState } from "react";
import "./Class.css";

function Classes() {
  const [classInfo, setClassInfo] = useState({});
  const [currTab, setCurrTab] = useState("");
  useEffect(() => {
    const d = {
      className: "ECS150",
      quarter: "WQ2021",
      professor: "Porquet",
      totalStudents: 150,
      sidebar: ["general", "homework-help", "off-topic", "Q&A"],
    };
    setClassInfo(d);
    setCurrTab(d.sidebar[0]);
  }, []);

  return (
    // <div id="body">
    //   <div id="sidebar">
    //   </div>
    //   <div id="main">
    //     <div className="Spacing"></div>
    //   </div>
    // </div>
    <main>
      <div id="left">

         <div className="spacing">
           <div className="classNameSidebar">{classInfo.className}</div>
           <label>{classInfo.quarter}</label>
           <ul>
             {classInfo.sidebar &&
               classInfo.sidebar.map((sidebar) => (
                 <li key={sidebar}>
                   <button
                     className="sidebarLinks"
                     onClick={() => setCurrTab(sidebar)}
                   >
                     {sidebar}
                   </button>
                 </li>
               ))}
           </ul>
         </div>
      </div>
      <div id="right">Right: Click to Toggle Left</div>
    </main>
  );
}
export default Classes;
